sceneList.script:
function inScene(itemName) {
    return isVisible(itemName) && !hasItem(itemName)
}

if(!getAttribute("window", "isOpen") && isVisible("Mortimor") && isVisible("Steve") && !isVisible("Sartorious")){
    if(!inScene("incense") && !inScene("kush filled thurible")) {
        play("Mortimor calls to you from the stall where he's working on the clogged toilet. \"Hey buddy, want to fetch me some incense? It reeks in here!\"")
    } else if (inScene("incense")) {
        play("Mortimor yells: \"Hey buddy, the incense ran out! Earn your keep and get us some more.\"")
    } else if(inScene("kush filled thurible")) {
        play("The bathroom is filled with thick, intoxicating smoke from your kush-filled thurible. Mortimor sits casually on the toilet, looking content.")
    }
}

actions.script:
singlePlay('You close the bowl of wool. It creaks ominously and crashes down with a shudder.', 'wtf.mpeg4')
setAttribute('Bowl of Wool', 'isOpen', false)
startTimer("bah", 5000, () => {
    singlePlay("The bowl of wool explodes! All of it seems to go nowhere.", "")
    removeItemFromScene("Bowl of Wool", "New Scene")
})

var creepingHorror = getAttribute('Sheep', 'creepingHorror') || 0
changeAttribute('Sheep', 'creepingHorror', creepingHorror + 1)

if (creepingHorror > 5) {
    singlePlay('BYAAAAAAAH!!!!!!!', 'byah.mpeg4')
}

if(getFlag('The Jesus', 'mad')){
    singlePlay(`The Jesus lights up and calms down. "Thanks!" says the Jesus. "That's some sweet sweet kush you got there. We should go take it to the Monks of Eternal Anguish®."\n`, "")
    addItemToInventory('The Jesus')
    switchScene('The Monastery Entrance')
    once()
} else {
    singlePlay(`"Nah, save it for the monks."`, "")
}

topics.script:
if (getFlag('Man on rack', 'notScreaming')) {
    play("\"Come to think of it, I haven't heard him screaming in a while. I'll go see what's the matter.\" Steve gets up and goes to the other room.")
    removeItemFromScene('Steve', 'Inside the Monastery')
    startTimer('steveReturns', 30000, function() {
        removeFlag("Man on rack", 'notScreaming')
    })
}else{
    play("Oh him? He's just here for some anguish.")
}

actions.script:
var responses = [{text: "Hey!", audio: ""}, {text: "Watch it!", audio: ""}, {text: "Stop it!", audio: ""}, {text: "Unhand me!", audio: ""}, {text: "What do you think you're doing?", audio: ""}, {text: "What did I do to you?", audio: ""}, {text: "Why are you doing this?", audio: ""}, {text: "What the hell?", audio: ""}, {text: "Oof", audio: ""}, {text: "Ow!", audio: ""}]
function inScene(itemName) {
    return isVisible(itemName) && !hasItem(itemName)
}


function isBathroomHotboxed(){
    return isVisible("Steve") && isVisible("Mortimor") && !getAttribute("window", "isOpen") && inScene("kush filled thurible") 
}

if(!getAttribute("Sartorious", "pushCounter")){
    setAttribute("Sartorious", "pushCounter", 1) 
    playRandom(responses)
}else{
    setAttribute("Sartorious", "pushCounter", 1 + getAttribute("Sartorious", "pushCounter"))
    if(getAttribute("Sartorious", "pushCounter") === 3){
        switchScene("Monastery Main Hall")
        removeItemFromScene("Sartorious", "Inside the Monastery")
        addItemToScene("Sartorious", "Monastery Main Hall")
        play("You push Sartorious through the doors of the Monastery Main Hall. \"Where are you taking me?!\" he cries.")
    }else if(getAttribute("Sartorious", "pushCounter") === 5){
        switchScene("Bathroom")
        removeItemFromScene("Sartorious", "Monastery Main Hall")
        addItemToScene("Sartorious", "Bathroom")
        if(isBathroomHotboxed()){
            play(`You push Sartorious into the bathroom, and block the exit. Everyone gets super baked. You all start talking about Life, The Universe and Stuff. Gingerly you pry, "So, what is the Ultimate knowledge?"
            "You want to know? I'll show ya!" boasts Mortimor. The others let out small groans of disapproval, but they are waaaay to baked to give a damn. Mortimor leaves only to return with an intracately carved wooden box about one foot long and six inches wide bewteen his hands. "Open it", he says.
            You look to the Jesus and he just nods somberly. You slowly open the box! 
            Inside are 3 collectable bobble heads, still bobbling from their journey to the bathroom.
            "the Ultimate Knowledge © 2017". The Jesus grabs the box from Mortimor. "Run!" he cries as he bounds out the door. Dazed and confused you look up at Mortimor who has death in his eyes. You take off sprinting behind the Jesus to destinations unknown.

            End of Part One.
            `)
        }else if(isVisible("Steve")){
            play(`"Oh god! It smells repugnant in here!" he cries. He pushes his way past you and returns to the courtyard. "Get some incense!" Mortimor calls out after him. Steve lets out a loud, extremely wet fart. You're mouth fills with vomit.`)
            removeItemFromScene("Sartorious", "Bathroom")
            addItemToScene("Sartorious", "Inside the Monastery")
            setAttribute("Sartorious", "pushCounter", 0)
        }else{
            play(`"I don't know why you've pushed me here but I will go no further!" he cries. He pushes his way past you and returns to the courtyard.`)
            removeItemFromScene("Sartorious", "Bathroom")
            addItemToScene("Sartorious", "Inside the Monastery")
            setAttribute("Sartorious", "pushCounter", 0)
        }
    }else{
        playRandom(responses)
    }
}

if (getAttribute('Cabinet', 'isOpen')) {
    removeItemFromScene('Laxative', 'Bathroom')
    play("You close the cabinet.")
} else {
    play("It's already closed.", '')
}

if (!getAttribute('Cabinet', 'isOpen')) {
    setAttribute('Cabinet', 'isOpen', true)
    if(!isVisible('Laxative')){
        play("The cabinet is empty except for an unusally large bottle of laxatives.")
        addItemToSceneAtIndex('Laxative', 'Bathroom', 1)
    }
} else {
    play("It's already open")
}

if (getAttribute("window", "isOpen")) {
    setAttribute("window", "isOpen", false)
    changeInSceneDescription("window", {text: "High up on one wall is a small window.", audio: ''})
    play("You close the window. It creaks closed.", "")
} else {
    play("It's already closed.", "")
}

if (currentSceneIs('Bathroom') && !getAttribute("window", "isOpen")) {
    play("The Jesus pulls out a lighter engraved with a skull and crossed guns, a snake crawling from one of the eye sockets. Wordlessly, he hands it to you. You light the incense and place it on the bathroom counter. The horrid smell seems to abate, or at least is somewhat obscured.")
} else {
    play("\"But I don't want any incense!\"")
}

if (currentSceneIs('Bathroom') && !getAttribute("window", "isOpen")) {
    play("The Jesus pulls out a lighter engraved with a skull and crossed guns, a snake crawling from one of the eye sockets. You light the kush-laced incense and place it on the bathroom counter. The smell of marijuana does little to combat the horrid stench, but Mortimor doesn't seem as bothered by it anymore.")
} else {
    play("\"But I don't want any incense!\"")
}

combinations.script:
if(!isVisible("Steve")){
    addItemToScene('Steve', 'bathroom')
    play(`You add the laxative to the tea while Steve is away. The pills dissolves quickly until it is completely unnoticable. You think you might have added too much...

    Moments later, Steve returns. He sits and takes a long drink from his hot cup of tea. He sits back in pure anguish. Then, his eyes buldge, there is an unexpected level of anguish in his sip! He sits up right and then burts into a sprint for the bathroom. He might be there for a while.`, "")
    changeInSceneDescription('Steve', {text: "Steve occupies one of the toilets. A foul and ungodly smell eminates from the stall", audio: ""})
    changeDescription("Steve", [{text: "He's in the stall. He doesn't sound like he's having fun." ,audio:""}])    
}else{
    play("I think he'll notice.")
}

if(!getFlag("toilet", "clogged")){
    if(hasItem("empty roll")){
        removeItemFromInventory("empty roll")
    }else{
        removeItemFromScene("empty roll", "Bathroom")
    } 
    play("You stuff the empty roll into the toilet and flush. Water begins to pool over the top soaking your sandals.", "")
    setFlag("toilet", "clogged")
    changeInSceneDescription("toilet", {text: "One of the toilets is full to the brim.", audio: ""})
    makeBroachable("Sartorious", "clogged toilet")
    makeBroachable("Mortimor", "clogged toilet")
    makeBroachable("Steve", "clogged toilet")
}

var output = []

output.push({text: "You replace the incense with some of your sweet, sweet kush. Enough to get like, 3 people really high.", audio:''})

if (currentSceneIs("bathroom")) {
    output.push({text: "The smell of marijuana does little to combat the horrid stench, but Mortimor doesn't seem as bothered by it anymore.", audio: ""})
} else {
    removeItemFromScene("incense", "library")
    addItemToInventory("kush filled thurible")
}

playMultiple(output)
