var directions = [
    'north',
    'east',
    'south',
    'west',
    'northwest',
    'northeast',
    'southwest',
    'southeast'
];
gameState.mazeAnswer = [
    'west',
    'south',
    'northwest',
    'east',
    'southeast'
];
function clearExits(sceneName) {
    var scene = findScene(sceneName);
    scene.exits = [];
}
function spitOut() {
    clearExits('Inside the Maze');
    addExit('Inside the Maze', 'south', 'The Hedge Maze', '', 'wptdahohcx');
    silentSwitchScene('The Hedge Maze');
    play('That took you back to the beginning of the maze! Spooky.', 'Vdv3EznyCQ');
    gameState.entered = false;
    gameState.spitOut = false;
    listExits();
}
function shuffleExits(chosen, dest) {
    clearExits('Inside the Maze');
    var num_exits = _.random(1, 4);
    var exits = [];
    if (chosen) {
        var dir = _.reject(directions, e => e === chosen);
        exits = _.sample(dir, num_exits);
        exits.push(chosen);
        exits = _.shuffle(exits);
    } else {
        exits = _.sample(directions, num_exits);
    }
    for (var e of exits) {
        if (dest) {
            addSilentExit('Inside the Maze', e, 'Inside the Maze', '', 'enrjaqmej7');
        } else {
            addSilentExit('Inside the Maze', e, 'Inside the Maze', '', 'yjij8jqzvw');
        }
    }
}
if (gameState.spitOut) {
    spitOut();
} else {
    if (gameState.entered) {
        if (gameState.lastDirection === gameState.progress[0] && !gameState.mazeWrong) {
            if (gameState.progress.length === 1) {
                clearExits('Inside the Maze');
                addExit('Inside the Maze', 'east', 'The Generator', 'that leads out of the maze! To the east, towards some sort of machine', 'cginq1a0rv');
                removeExit('The Backyard', 'north');
                addExit('The Backyard', 'north', 'The Generator', 'north, through the maze, to the generator', 'gx6vbfxbgd');
                setFlag('generator', 'navigated');
                listExits();
            } else {
                gameState.progress.shift();
                shuffleExits(gameState.progress[0]);
                listExits();
            }
        } else {
            shuffleExits(false, true);
            listExits();
            gameState.spitOut = true;
            gameState.entered = false;
        }
    } else {
        gameState.progress = gameState.mazeAnswer.slice();
        gameState.entered = true;
        clearExits('Inside the Maze');
        shuffleExits(gameState.progress[0]);
        listExits();
    }
}

