topics.script:
if(!isBroachable("butler", "kennel master")){
makeBroachable("butler", "kennel master")
play("\"Yes, by that wretched, drunken kennel master. He spends all his time with uncivilized beasts and so he acts like them. We've never seen eye to eye, but this is too far, sir! Too far! I simply came in the closet to change the sheets, and then while my back was turned, the brute slammed the door behind me and turned the key!\"\n\n \*\*New topic available: \"Kennel master?\"\*\*")
makeUnbroachable("butler", "locked in")
}

if (!isBroachable("butler","key")){
    play("\"Yes. The wretch looks over the master's scent hounds. But I'm afraid the grounds are quite overhunted, and the idleness has driven the man into the bottom of a bottle. On a number of occasions he has behaved inappropriately around my dear fiancé, and I daresay he is likely to go further, if he hasn't already! In fact, it wouldn't surprise me at all if my imprisonment is part of his muddleheaded scheme to snatch her away from me. You must help me, you must find the key and get me out of here!\"\n\n\*\*New topic added: \"Key?\"\*\*")
    makeBroachable("butler", "key")
    makeUnbroachable("butler", "kennel master")
}

if (hasItem("key")){
    play("\"What?! You have the key?! Then what are you waiting for, man? Put it in the door and get me out of here!\"")
}else{
    play("\"Yes, you know, the little metal thingy you put in the door and twist? You have heard of doors haven\'t you? Or is that concept foreign to you as well?! Oh, I'm sorry if I'm being snippy, but I've been in here for ages and ages, you have to get me out! Find that mongrel Kennel Master, he\'s the key to finding...well. The key.\"")
}

if (!isBroachable("butler","weird word")){
    play("\"The Master? Oh, well I’m closer to him than anyone. As his butler, I often find myself assisting him with his studies of the Nexus, he is a most brilliant and powerful man, I can tell you. He often goes off like this for periods of time, to collect things for his research on the Ki’udba Alliga. Oh sod-it, Archie -- When will you learn to keep your mouth shut?\"\n\n\*\*\"New topic added: \"That weird word he just said?\"\*\*")
    makeBroachable("butler","weird word")
    makeBroachable("freebutler", "weird word")
}else{
    play("\"The Master? Oh, well I’m closer to him than anyone. As his butler, I often find myself assisting him with his studies of the Nexus, he is a most brilliant and powerful man, I can tell you. He often goes off like this for periods of time, to collect things for his research on the Ki’udba Alliga. Oh sod-it, Archie -- When will you learn to keep your mouth shut?\"")
}

actions.script:
changeInSceneDescription("butler", {text:"The butler is locked inside.", audio: ""})
once()

if (!isBroachable("maid", "closet")){
    makeBroachable("maid", "closet")
}

if (getFlag("curtain", "cut")){
    play("It's long to put in your jacket, but it could be useful here.")
}else{
play("The curtain is attached to a rod on the ceiling. It won't come off.")
}

if (getFlag("curtain", "railed")){
    switchScene("the patio")
}else{
    play("You open and close the curtains. They seem to be in working order.")
}

if(!getAttribute("eggs", "pickup")){
    setAttribute("eggs", "pickup", [{text: "You grab the eggs, but as you try to leave the coop, the chicken zeroes in on you and starts pecking like a hen out of hell! You run to escape its fury, but it is impossibly fast and unrelentingly viscious. Finally, in desperation, you return the eggs and thankfully, as soon as you're out of its radius, it forgets about you.", audio:""}, {text: "You nab the eggs again but before you can fly the coop, the chicken lands a monster peck right on the back of your hand. You recoil in stinging pain, dropping the eggs back in the nest and retreat once again.", audio:""}, {text: "You've learned your lesson. There's no way that demon chicken is letting you empty her nest."}])
}
playInSequence(getAttribute("eggs", "pickup"))

removeItemFromInventory("eggs")
setFlag("chef", "haseggs")

if (!getFlag("chef", "hasrecipe")){
    play("You give the eggs to the chef:\n\n \"You managed to get some eggs from the old broody hen, did ya? No small feat. Thank you! Now if I can just find one of my recipes I\'ll be able to put these to good use!\"")

}else if(getFlag("chef", "hasrecipe")){
    if((getFlag("chef", "hasbacon") && !getFlag("stove", "working")) || (getFlag("chef", "hasbacon") && getFlag("stove", "working") && !getFlag("gas line", "gassed"))){
        play("\"Wow, would you look at those eggs! Fantastic. Well done, my boy. Well done. As soon as we get that stove working, I can poach these puppies and add them to our benedict.\"")
    }

    else if(!getFlag("chef", "hasbacon") && getFlag("stove", "working") && getFlag("gas line", "gassed")){
        play("\"Wow, would you look at those eggs! Fantastic. Well done, my boy. Well done. As soon as we get that bacon, I can poach these puppies and add them to our benedict.\"")
    }
    else if(getFlag("chef", "hasbacon") && getFlag("stove", "working") && getFlag("gas line", "gassed")){
        play("\"Wow, would you look at those eggs! Fantastic. Well done, my boy. Well that\'s it! I have everything I need to get cookin\'\"\n\n The chef gathers the ingredients and turns on the stove. You watch as the chef masterfully whips up a fresh batch of Eggs Benedict, the smoky smells of bacon and buttery hollandaise fill the room with a decadent aroma.\n\n\"Ohh that smell! Yes that wonderful smell. I remember now. The passcode, it\'s 2342, a year of some importance to the Master as I recall.\"")
        setFlag("keypad", "codeready")
        addItemToScene("eggs benedict_ob", "the kitchen")
        makeUnbroachable("chef", "eggs benedict")
    }else{
        play("\"Wow, would you look at those eggs! Fantastic. Well done, my boy. Well done. As soon as we get some bacon, and a working stove, I can poach these puppies and add them to our benedict.\"")
    }
}

if(getFlag("slot", "used") && getFlag("stone tablet", "known")){
play("The slot seems meant for the time rune.")
}
else if(getFlag("slot", "used")){
play("The slot seems to be meant for your stone tablet.")
}else{
play("The slot is only a few inches long and wide -- something is meant to fit in here, but certainly not anything as large as a lighthouse lantern. The markings on the pedestal must be some sort of ancient language.")
}

if (!getFlag("crank", "cranked") && getFlag("weathervane", "starrynight")){
play("You start cranking and the metal blinds blocking the lighthouse windows draw back to reveal a sky filled with purple stars, both brilliant and bizzare. There is no sea, only a world swaddled in the unnatural light of these too-numeerous stars.\n\n ...Just where the hell are you?\n\n As you finish retracting the blinds, the crank breaks off from the wall and falls to the ground.")
changeInSceneDescription("crank", {text: "The crank lies on the floor.", audio: ""})
removeItemFromScene("blinds","The Lantern Room")
addItemToScene("blinds_done", "The Lantern Room")
changeInSceneDescription("pedestal", {text: "At the center of the room there is a round pedestal where it seems like a lighthouse lantern should go.", audio:""})
changeInSceneDescription("telescope", {text: "Mounted above the pedestal is a giant metal cylinder, resembling an observatory telescope. The larger end points toward the tower's windows, and the purple starry night sky.", audio: ""})
setFlag("crank", "cranked")}
else if (!getFlag("crank", "cranked")){
play("You start cranking and the metal blinds blocking the lighthouse windows draw back to reveal a cloud filled horizon. There is no sea. There is nothing but a world of dark purple clouds that seem to esconce the world in a sphere...\n\n ...Just where the hell are you?\n\n As you finish retracting the blinds, the crank breaks off from the wall and falls to the ground.")
changeInSceneDescription("crank", {text: "The crank lies on the floor.", audio: ""})
removeItemFromScene("blinds","The Lantern Room")
addItemToScene("blinds_done", "The Lantern Room")
changeInSceneDescription("pedestal", {text: "At the center of the room there is a round pedestal where it seems like a lighthouse lantern should go.", audio:""})
changeInSceneDescription("telescope", {text: "Mounted above the pedestal is a giant metal cylinder, resembling an observatory telescope. The larger end points toward the tower's windows, and the cloudy night sky.", audio: ""})
setFlag("crank", "cranked")
}else{
    play("A crank is only as good as the thing it's cranking.")
}

if (getFlag("crank", "cranked")){
    play("You take the crank and put it in your coat.")
    takeItem("crank")
    once()
}else{
    play("The crank is anchored in the wall")
}

if(hasItem("bouquet") || hasItem("bouquet with love letter") || hasItem("bouquet with hate note")){
    play("You already have plenty of petunias.")
}else{
    play("You pluck a handful of the petunias. They smell wonderful.")
    addItemToInventory("bouquet")
}

var counter = getAttribute('bike', 'counter')
if(!counter){
    setAttribute('bike', 'counter', 1)
    play("You pull at the bike, discovering that the rusty handlebars seem loose.")
}else if(counter === 1){
    setAttribute('bike', 'counter', 2)
    play("You pull even harder -- the handlebars seem quite loose now.")
}else if(counter === 2){
    setAttribute('bike', 'counter', 3)
    addItemToScene("handlebars","the front lawn")
    changeInSceneDescription('bike', {text: "A pair of handlebars lies on the grass. The decapitated bike remains stuck to the lampost, which illuminates", audio: ""})
    removeAlias("bike","handlebars")
    play("With a great pull, you wrench the handlebars off the bike! The rusty handlebars fall to the ground.")
    changeDescription("bike",[{text: "A bike without handlebars rusted onto a lampost. You're sure there's a metaphor here, but poetry was never your strong suit."}])

}else{
    play("The rest of this bike isn't going anywhere.")
}

if (!getFlag("freebutler", "maidbroached")){
play("You're not in the habit of giving flowers to strange men without good cause.")
}

else if (getFlag("maid","firstdeliver")){
    play("You hand the bouquet to the butler.\n\n\"Perfect again!\"\n\n He sticks an envelope in the bouquet and hands it back to you.\n\n\"Once more unto the breach, dear fellow!\"")
    replaceItemInInventory("bouquet", "bouquet with love letter")
    setFlag("freebutler", "redo")
    setFlag("freebutler", "waiting")
    makeUnbroachable("freebutler", "maid_1")
    makeUnbroachable("freebutler", "flowers delivered")
    makeBroachable("freebutler", "flowers")


}else{
play("You hand the bouqet to the butler.\n\n\"Yes! Yes, those are perfect. Petunias are her absolute favorite.\"\n\n He sticks an envelope in the bouquet and hands it back to you.\n\n\"In the service of true love, please deliver those for me\"")
replaceItemInInventory("bouquet", "bouquet with love letter")
setFlag("freebutler", "waiting")
makeUnbroachable("freebutler", "maid_1")
makeBroachable("freebutler", "flowers")
makeUnbroachable("freebutler", "flowers delivered")
}

removeItemFromInventory("boar meat")
setFlag("chef", "hasbacon")

if (!getFlag("chef", "hasrecipe")){
    play("The you give the chef the boar meat: \n\n\"This boar meat could make for some interesting bacon! Now if I could just find one of my recipes...\"")
}

else if (getFlag("chef", "hasrecipe")){
    if((getFlag("chef", "haseggs") && !getFlag("stove", "working")) || (getFlag("chef", "haseggs") && getFlag("stove", "working") && !getFlag("gas line", "gassed"))){
        play("\"Killed yourself a boar did you? That meat should make for some interesting bacon! Now, as soon as we get that stove working, our Benedict will be in business!\"")
    }

    else if(!getFlag("chef", "haseggs") && getFlag("stove", "working") && getFlag("gas line", "gassed")){
         play("\"Killed yourself a boar did you? That meat should make for some interesting bacon! Now, as soon as we get some eggs, our Benedict will be in business!\"")
    }
    else if(getFlag("chef", "haseggs") && getFlag("stove", "working") && getFlag("gas line", "gassed")){
        play("\"Killed yourself a boar did you? That meat should make for some interesting bacon! Well that\'s it! I have everything I need to get cookin\'\"\n\n The chef gathers the ingredients and turns on the stove. You watch as the chef masterfully whips up a fresh batch of Eggs Benedict, the smoky smells of bacon and buttery hollandaise fill the room with a decadent aroma.\n\n\"Ohh that smell! Yes that wonderful smell. I remember now. The passcode, it\'s 2342, a year of some importance to the Master as I recall.\"")
        setFlag("keypad", "codeready")
        addItemToScene("eggs benedict_ob", "the kitchen")
        makeUnbroachable("chef", "eggs benedict")
    }else{
          play("\"Killed yourself a boar did you? That meat should make for some interesting bacon! Now, as soon as we get some eggs and a working stove, our Benedict will be in business!\"")
    }
}

if (!getFlag("shed", "known") && getFlag("broken window", "open")){
    changeExitDescription("The Dog Yard", "west", "west into the Kennel House", "")
    setFlag("shed", "known")
}
else if (!getFlag("shed", "known")){
    changeExitDescription("The Dog Yard", "west", "west into the Kennel House.", "")
    setFlag("shed", "known")
}
play("It appears to be the kennel house. It's decaying wood frame has seen better days.")
changeInSceneDescription("shed", {text: "A group of dogs run and play in a fenced in area next to the kennel house.", audio: ""})

if(!getAttribute("table", "look")){
    setAttribute("table", "look", [{text: "Upon further inspection you see that the table is attached to the wall support by a metal clamp.", audio: ""}, {text: "Even with the clamp, the table is barely hanging on the the wall", audio: ""}])
}
playInSequence(getAttribute("table", "look"))
changeInSceneDescription("clamp", {text: "A clamp keeps it cinched to the wall.", audio: ""})

if (!getFlag("key","takeable")){
    play("\"Keep your paws away from that! Making sure that prig of a butler stays locked away is the undivided focus of my every waking hour!\"")
}else{
    play("You pick up the key and stash it away.")
    takeItem("key")
    once()
}

if (getFlag("generator", "navigated")){
switchScene("The Generator")
}else{
    switchScene("The Hedge Maze")
}

if (!(getFlag("broken window", "open"))){
    play("You reach through the broken pane and unlatch the window. It swings open, and you crawl inside taking care not to cut yourself on any glass shards.\n\n")
    changeDescription("broken window", [{text: "The broken window is open. Inside the house you can make out a bookshelf and desk. It looks quite cozy.", audio: ""}])
    setFlag("broken window", "open")
    switchScene("The Study")
    addExit("The Dog Yard", "up", "The Study", "up into the mansion's window to the Study.", "")
    if (getFlag("shed", "known")){
    changeExitDescription("The Dog Yard", "west", "west into the Kennel House", "")
    }else if (!getFlag("shed", "known")){
    changeExitDescription("The Dog Yard", "west", "west into the shed", "")
    } 
}else{
    switchScene("The Study")
}

if (getFlag("grandfather clock", "solved")){
    play("The clock's time now reads 1-2-3. But it wasn't exactly easy.")
}
else if(getAttribute("hour hand", "time") != 12){
    play("As you examine the clock, its hands suddenly snap back to their original 12 o'clock position. Spooky.")
    setAttribute("hour hand", "time", 12)
}else{
    play("The grandfather clock is very old, and very large -- even a bit taller than you. Mysterious symbols are etched into the glass of its front cabinet. The gears inside seem to be turning, but the clock face is frozen on 12 midnight.") 
}

if(getAttribute("hour hand", "time") != 12){
    play("As you examine the clock, its hands suddenly snap back to their original 12 o'clock position. Spooky.")
    setAttribute("hour hand", "time", 12)
}else{
    play("There are numerals ranging from 1 to 12. Both the hour hand and the minute hand are currently pointing in the 12 o'clock position.") 
}

if(getAttribute("hour hand", "time") != 12){
    play("After moving the hour hand, the minute hand looks eager to be set.")
}else{
    play("Currently, the minute hand is set to zero minutes.") 
}

if(getAttribute("hour hand", "time") != 12){
    play("The hour hand is holding its new position almost begrudingly.")
}else{
    play("Currently, the hour hand is set to 12 'o clock.") 
}

if (getFlag("gas tank", "untanked")){
switchScene("The Dark Secret Lab")
}
else if (getFlag("knob", "initialtwist")){
switchScene("The Secret Lab")
}else{
switchScene("the basement")
}

if(!getAttribute("hands", "used")){
    setAttribute("hands", "used", [{text: "You feel around in the darkness for a light switch, a lamp, anything. You hands land on something hard and bulbous with a small knob that has a familiar feel.", audio:""}, {text: "You try again, all you can feel are a few large objects, and some small bulbous object with a knob on it.", audio:""}, {text: "The bulbous object with the knob is the only thing you can make any sense of.", audio: ""}])
}
playInSequence(getAttribute("hands", "used"))
changeInSceneDescription("look item", {text: "It is pitch black, but after feeling around you've discovered a small bulbous object with knob on it"})

if(!getAttribute("hands", "used")){
    setAttribute("hands", "used", [{text: "You feel around in the darkness for a light switch, a lamp, anything. You hands land on something hard and bulbous with a small knob that has a familiar feel.", audio:""}, {text: "You try again, all you can feel are a few large objects, and some small bulbous object with a knob on it.", audio:""}, {text: "The bulbous object with the knob is the only thing you can make any sense of.", audio: ""}])
}
playInSequence(getAttribute("hands", "used"))
changeInSceneDescription("look item", {text: "It is pitch black, but after feeling around you've discovered a small bulbous object with knob on it"})

play("You pick up up the lantern.")
takeItem("lantern")

if (!hasItem("gas tank")){
    addItemToInventory("gas tank")
}

once()

if(getFlag("gas tank", "untanked")){
    play("It is a small, portable gas tank.")

}else{
    play("The gas tank is screwed into the bottom of the lantern.")
}

if(getFlag("gas tank", "has")){
    play("You already have that.")
}else if (getFlag("gas line", "first gas")){
    play("You unscrew the gas tank from the gas line and pocket the gas tank.")
    changeInventoryName("gas tank", "", "")
    changeInSceneDescription("gas line", {text: "On the side of the building, a gas line crawls along the mansion's wood paneling.", audio: ""})
    changeDescription("gas line", [{text: "The gas line runs along the side of the mansion and eventually cuts through the wall into some room inside. At one end there is a small nozzle that looks like it\'s missing a fuel source.", audio: ""}])
    removeFlag("gas line", "gassed")
    removeFlag("stove", "working")
    setFlag("gas tank", "has")
    changeInventoryName("gas tank", "gas tank", "")

}

else{
    if(!getFlag("generator", "powered")){
        play("You unscrew the gas tank from the lantern, and the light goes out. You pocket the gas tank.")
        removeItemFromInventory("gas tank")
        takeItem("gas tank")
        changeDescription("lantern", [{text: "It is a gas-lit lantern, with no gas to light it.", audio: ""}])
        removeExit("the study", "down")
        addExit("the study", "down", "the dark secret lab")
        setFlag("gas tank", "untanked")
        setFlag("gas tank", "has")
        changeInventoryName("gas tank", "gas tank", "")
    }
    if (getFlag("generator", "powered")){
        play("You unscrew the gas tank from the lantern, and the light goes out. You pocket the gas tank.")
        removeItemFromInventory("gas tank")
        takeItem("gas tank")
        changeInSceneDescription("lantern",{text: "The lantern sits dim on a nearby shelf.", audio: ""})
        changeDescription("lantern", [{text: "It is a gas-lit lantern, with no gas to light it.", audio: ""}])
        setFlag("gas tank", "untanked")
        setFlag("gas tank", "has")
        changeInventoryName("gas tank", "gas tank", "")
    }
    }if(currentSceneIs("the secret lab") && !getFlag("generator", "powered")){
    switchScene("The Dark Secret Lab")
    }


    if (!hasItem("lantern")){
    addItemToScene("dark lantern", "the dark secret lab")
    addItemToScene("dark knob", "the dark secret lab")
    }

if (!getFlag("keypad", "codeready")){
    play("It might be the security system for the doors. There is a small display at the top, but it's completely dark.") 
}else{
    play("It might be the security system for the doors. There is a small electronic display at the top.")
}

if(!getAttribute("keypad", "press")){
    setAttribute("keypad", "press", [{text: "You press a few buttons but nothing happens. It occurs to you that it might be electronic. Too bad the power's out.", audio:""}, {text: "Without power, it's no use.", audio:""}])
}

